String? findCityForCoordinates(double latitude, double longitude) {
  Point point = Point(x: latitude, y: longitude);

  for (var city in turkishCities) {
    if (Poly.isPointInPolygon(point, city.getFormattedCoordinates())) {
      return city.name;
    }
  }

  return null;
}